# input = """
#      |          
#      |  +--+    
#      A  |  C    
#  F---|--|-E---+ 
#      |  |  |  D 
#      +B-+  +--+ 
                
# """

ABC = []
for i in range(ord('A'), ord('Z') + 1):
    ABC.append(chr(i))

matrix = input.splitlines()[1:]

UP = 0
RIGHT = 1
DOWN = 2
LEFT = 3


def next_pos(y, x, dir):
    if dir == DOWN:
        return y+1, x
    if dir == UP:
        return y-1, x
    if dir == RIGHT:
        return y, x+1
    if dir == LEFT:
        return y, x-1


def main():
    x = matrix[0].find("|")
    y = 0
    dir = DOWN
    ret = 1

    while True:
        y, x = next_pos(y, x, dir)
        char = matrix[y][x]
        if char in ["|", "-"] + ABC:
            ret += 1
            continue
        if char == "+":
            ret += 1
            if dir % 2: # horizontal
                if matrix[y-1][x] !=  " ":
                    dir = UP
                else:
                    dir = DOWN
            else: # vertical
                if matrix[y][x-1] !=  " ":
                    dir = LEFT
                else:
                    dir = RIGHT
            continue

        if char == " ":
            break

    print(ret)
            

main()
